import React, {PureComponent} from 'react';
import {DetailWrapper, Header, Content} from './style';

class Detail extends PureComponent {
    render() {
        return (
            <DetailWrapper>
                <Header>零成本辞退员工的六个套路</Header>
                <Content>
                    <img alt = 'no support' src = '//upload-images.jianshu.io/upload_images/9350473-38cde210d1feb103.jpg?imageMogr2/auto-orient/strip|imageView2/2/w/909/format/webp'></img>
                    <p><b>作为职场人，稍微了解一点劳动法常识的人都知道，公司辞退员工是要承担很大的风险的。特别是对于那些工作十年以上的老员工来说更是如此。究其原因其实无非就是补偿金的承担问题。如今市场环境不乐观，公司裁员早就是箭在弦上，不得不发了。公司零成本辞退员工的6种方式，满满的都是套路！</b></p>
                    <p>作为职场人，稍微了解一点劳动法常识的人都知道，公司辞退员工是要承担很大的风险的。特别是对于那些工作十年以上的老员工来说更是如此。究其原因其实无非就是补偿金的承担问题。如今市场环境不乐观，公司裁员早就是箭在弦上，不得不发了。公司零成本辞退员工的6种方式，满满的都是套路！</p>
                </Content>
            </DetailWrapper>
        )
    }
}

export default Detail;
