import React, {Component} from 'react';
import {connect} from 'react-redux';
import {TopicWrapper,
    TopicItem} from '../style';

class Topic extends Component {

    render() {
        const {list} = this.props;
        return (
            <TopicWrapper>
                {list.map((item)=> {
                    return (
                        <TopicItem key = {item.id}>
                            <img alt = 'no support' src = {item.imgUrl}></img>
                        </TopicItem>
                    )
                })}
                <TopicItem>
                    <img className = 'topic-img' alt = 'no support img!!!' src = '//upload-images.jianshu.io/upload_images/13910150-3c33a8d64aa623bc.png?imageMogr2/auto-orient/strip|imageView2/1/w/360/h/240'></img>
                    社会热点
                </TopicItem>
            </TopicWrapper>
        )
    }
}

const mapState = (state) => ({
    list: state.getIn(['home', 'topicList'])
})

export default connect(mapState, null)(Topic);
