import React, {Component} from 'react';
import {CSSTransition} from 'react-transition-group';
import {connect} from 'react-redux';
import {
    HeaderWrapper,
    Logo,
    Nav,
    NavItem,
    NavSearch,
    Addition,
    Button,
    SearchWrapper,
    SearchInfo,
    SearchInfoTitle,
    SearchInfoSwitch,
    SearchInfoItem,
    SearchInfoList
} from './style';
import {actionCreators }from './store/index';

class Header extends Component {

    getListArea = (show, list, handleMouseEnter, mouseIn, handleMouseLeave) => {
        if (show || mouseIn) {
            return (
                <SearchInfo onMouseEnter = {handleMouseEnter}
                onMouseLeave={handleMouseLeave}
                >
                    <SearchInfoTitle>热门搜索
                    <i className = 'iconfont'>&#xe862;</i>
                        <SearchInfoSwitch>

                            换一换</SearchInfoSwitch>
                    </SearchInfoTitle>
                    <SearchInfoList>
                        {list.map((item,index) => {
                            return (<SearchInfoItem key = {index}>{item}</SearchInfoItem>)
                        })}
                    </SearchInfoList>
                </SearchInfo>
            )
        }
    }

    render() {
        const {mouseIn, focused, handleInputFocus, handleInputBlur, list, page, handleMouseEnter, handleMouseLeave} = this.props;
        let pageList = [];
        for (let i = page * 10; i < (page + 1) * 10; i ++) {
            pageList.push(list[i]);
        }
        return (
            <HeaderWrapper>
                <Logo /*href = "/"*/></Logo>
                <Nav>
                    <NavItem className = 'left active'>首页</NavItem>
                    <NavItem className = 'left'>下载App</NavItem>
                    <NavItem className = 'right'>登陆</NavItem>
                    <NavItem className = 'right'>
                        <i className = 'iconfont'>&#xe636;</i>
                    </NavItem>
                    <SearchWrapper>
                        <CSSTransition
                        in = {focused}
                        timeout = {200}
                        classNames = 'slide'
                        >
                            <NavSearch
                            onFocus = {handleInputFocus}
                            onBlur = {handleInputBlur}
                            className = {(focused||mouseIn) ? 'focused' : ''} 
                            ></NavSearch>
                        </CSSTransition>
                        <i 
                        className = {focused ? 'focused iconfont' : 'iconfont'} 
                        >&#xe795;</i>
                        {this.getListArea(focused, pageList, handleMouseEnter, mouseIn, handleMouseLeave)}
                    </SearchWrapper>
                    <Addition>
                        <Button className = 'reg'>注册</Button>
                        <Button className = 'writter'>
                        <i className = 'iconfont'>&#xe607;</i>
                            写文章</Button>
                    </Addition>
                </Nav>
            </HeaderWrapper>
        )
    }
}

const mapState = (state) => {
    return {
        // focused: state.get('header').get('focused')
        focused: state.getIn(['header', 'focused']),
        list: state.getIn(['header', 'list']),
        page: state.getIn(['header', 'page']),
        totalPage: state.getIn(['header', 'totalPage']),
        mouseIn: state.getIn(['header', 'mouseIn'])
    }
}

const mapDispatch = (dispatch) => {
    return {
        handleInputFocus() {
            dispatch(actionCreators.getList());
            const action = actionCreators.searchFocus();
            dispatch(action);
        },
        handleInputBlur() {
            const action = actionCreators.searchBlur();
            dispatch(action);
        },
        handleMouseEnter() {
            const action = actionCreators.mouseEnter();
            dispatch(action);
        },
        handleMouseLeave() {
            const action = actionCreators.mouseLeave();
            dispatch(action);
        }
    }
}

export default connect(mapState, mapDispatch)(Header);
